package dev.oliver.todobackend.entity;import jakarta.persistence.*;@Entity@Table(name="todo_account")public class TodoAccount {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name="id")    private int id;    @Column(name="username")    private String userName;    @Column(name="password")    private String password;    @Column(name="email")    private String email;    public TodoAccount() {}    public TodoAccount(String userName, String password, String email) {        this.userName = userName;        this.password = password;        this.email = email;    }    public void setId(int id) {        this.id = id;    }    public void setUserName(String userName) {        this.userName = userName;    }    public void setPassword(String password) {        this.password = password;    }    public void setEmail(String email) {        this.email = email;    }    public int getId() {        return id;    }    public String getUserName() {        return userName;    }    public String getPassword() {        return password;    }    public String getEmail() {        return email;    }}