package dev.oliver.todobackend.entity;public class TodoAccount {    private String userName;    private String password;    private String email;    public TodoAccount(String userName, String password) {        this.userName = userName;        this.password = password;    }    public TodoAccount(String userName, String password, String email) {        this.userName = userName;        this.password = password;        this.email = email;    }    public void setUserName(String userName) {        this.userName = userName;    }    public String getUserName() {        return userName;    }    public void setPassword(String password) {        this.password = password;    }    public String getPassword() {        return password;    }    public void setEmail(String email) {        this.email = email;    }    public String getEmail() {        return email;    }}