package dev.oliver.todobackend.entity;import jakarta.persistence.*;import lombok.Getter;import lombok.Setter;@Getter@Setter@Entity@Table(name="todo_account")public class TodoAccount {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name="id")    private int id;    @Column(name="username")    private String userName;    @Column(name="password")    private String password;    @Column(name="email")    private String email;    public TodoAccount() {}    public TodoAccount(String userName, String password, String email) {        this.userName = userName;        this.password = password;        this.email = email;    }}